#include <iostream>
#include <vector>
#include <unordered_map>
#include <unordered_set>
#include <algorithm>
using namespace std;


class Solution {
public:
    vector<int> findAnagrams(string s, string p) {
        //创建两个哈希表
        unordered_map<char,int>need,window;
        //定义一个返回容器
        vector<int>returnValue;
        int left=0,right=0;
        int vaild=0;
        for(char c:p)
            need[c]++;
        //滑动框右边界
        while(right<s.size())
        {
            char c = s[right];
            right++;
            //窗口内的更新
            if(need.count(c))
            {
                window[c]++;
                if(window[c]==need[c])
                    vaild++;
            }
        
        //缩小滑动框的左边界
        while (right-left>=p.size())
        {
            //将滑动框的起始索引添加至返回数组
            if(vaild==need.size())
                returnValue.push_back(left);
           char c = s[left];
           left++;
           if(need.count(c))
           {
                
                if(window[c]==need[c])
                    vaild--;
                window[c]--;
           }
        }
    }
        
        return returnValue;
    }
};

int main()
{
    string s = "";
    string p = "yqrbgjdwtcaxzsnifvhmou";
    vector<int>nums;
    Solution so;
    nums = so.findAnagrams(s,p);
    for(auto &x:nums)
    {
        cout<<x<<endl;
    }
}